import React, { useState } from 'react';
import {
    MDBBtn,
    MDBContainer,
    MDBRow,
    MDBCol,
    MDBCard,
    MDBCardBody,
    MDBInput,
    MDBIcon,
    MDBCheckbox,
    MDBTabsLink,
    MDBCardLink,
    MDBNavbarLink,
    MDBTabs,
    MDBTabsItem,
    MDBCardImage
}
    from 'mdb-react-ui-kit';
import { Button } from 'reactstrap';
import '../styles/Login.css';
import { Link, useNavigate } from 'react-router-dom';
import { toast } from 'react-toastify';
import { loginUser as loginUserApi } from '../services/user'
function Login() {
    const [email, setEmail] = useState('')
  const [password, setPassword] = useState('')
    const navigate = useNavigate();


    const loginUser = async () => {
        if (email.length == '') {
          toast.error('Please enter email')
        } else if (password.length == '') {
          toast.error('Please enter password')
        } else {
          // call register api
          const response = await loginUserApi(email, password)

          // parse the response
          if (response['status'] === 'success') {
            // parse the response's data and extract the token
            const { token, name, mobile, profileImage } = response['data']

            // store the token for making other apis
            sessionStorage['token'] = token
            sessionStorage['name'] = name
            sessionStorage['mobile'] = mobile
            sessionStorage['profileImage'] = profileImage

            // update global store's authSlice with status = true
            dispatch(login())

            toast.success(`Welcome ${name} to store application`)

            // go back to login
            navigate('/product-gallery')
          } else {
            toast.error('Invalid user name or password')
          }
        }
      }

    return (
        <MDBContainer fluid>



            <MDBRow className='d-flex justify-content-center align-items-center h-100'>
                <MDBCol col='12'>

                    <MDBCard className='bg-white my-5 mx-auto' style={{ borderRadius: '1rem', maxWidth: '500px' }}>
                        <MDBCardBody className='p-5 w-100 d-flex flex-column'>

                            <h2 className="fw-bold mb-2 text-center">Sign in</h2>


                            <p className="text-white-50 mb-3">Please enter your login and password!</p>

                            {/* <MDBInput wrapperClass='mb-4 w-100' label='Email address' id='formControlLg' type='email' size="lg"/>
              <MDBInput wrapperClass='mb-4 w-100' label='Password' id='formControlLg' type='password' size="lg"/> */}

                            <div className='mb-3'>
                                <label htmlFor=''>Email</label>
                                <input
                                    type='text'
                                    className='form-control'
                                    onChange={(e) => {
                                        setEmail(e.target.value)
                                    }}
                                />
                            </div>
                            <div className='mb-3'>
                                <label htmlFor=''>Password</label>
                                <input
                                    type='password'
                                    className='form-control'
                                    onChange={(e) => {
                                        setPassword(e.target.value)
                                    }}
                                />
                            </div>

                            <MDBCheckbox name='flexCheck' id='flexCheckDefault' className='mb-4' label='Remember password' />




                            <button className="LoginRegisterButton text-white btn-lg mb-3">Login</button>

                            <a className="small text-muted mb-3" href="#!">Forgot password?</a>
                            <p className="mb-5 pb-lg-2" style={{ color: '#393f81' }}>Don't have an account? <a href="/register" style={{ color: '#393f81' }}>Register here</a></p>


                        </MDBCardBody>
                    </MDBCard>

                </MDBCol>
            </MDBRow>

        </MDBContainer>


    );
}

export default Login;
